for sheet_name in sheet_list:
    sheet = excel_file[sheet_name]
    for row_number in selected_rows:
        row_data = []
        for col_num in range(6, 105):
            cell = sheet.cell(row=row_number, column=col_num)
            row_data.append(float(cell.value))
        #print(f"Satır {row_number} verileri: {row_data}")
        print(row_number)
        # Seçilen dağılım türüne göre uygunluğu kontrol etme
        if selected_distr == 'normal':
            params = scipy.stats.norm.fit(row_data)
            ks_test = scipy.stats.kstest(row_data, 'norm', params)
        elif selected_distr == 'üstel':
            params = scipy.stats.expon.fit(row_data)
            ks_test = scipy.stats.kstest(row_data, 'expon', params)
        elif selected_distr == 'beta':
            params = scipy.stats.beta.fit(row_data)
            ks_test = scipy.stats.kstest(row_data, 'beta', params)
        elif selected_distr == 'lognorm':
            params = scipy.stats.lognorm.fit(row_data)
            ks_test = scipy.stats.kstest(row_data, 'lognorm', params)

        elif selected_distr == 'weibull':
            params = scipy.stats.weibull_min.fit(row_data)
            ks_test = scipy.stats.kstest(row_data, 'weibull_min', params)
        elif selected_distr == 'uniform':
            params = scipy.stats.uniform.fit(row_data)
            ks_test = scipy.stats.kstest(row_data, 'uniform', params)
        # Uygunluk sonucu yazdırma
        if ks_test.pvalue >= 0.05:
            print(f"Veriler {selected_distr} dağılıma uygundur.")
            result = [sheet_name, row_number, selected_distr, 'uygun']
        else:
            print(f"Veriler {selected_distr} dağılıma uymaz.")
            result = [sheet_name, row_number, selected_distr, 'uygun değil']
        results.append(result)
